import React from "react";
import './FeaturedMovie.css';

export default ({item})=>{
  return (

<section className="featured" style={{
  backgroundSize:'cover',
  backgroundPosition:'center',
  backgroundImage:`url(https://image.tmdb.org/t/p/original${item.backdrop_path})`
}}>
  <div className="featured--vertical">
    <div className="featured--horizontal"></div>
    <div className="featured--name">{item.name}</div>
    <div className="featured--info">
      <div className="featured--points">{item.vote_average} score</div>
      <div className="featured--year">2099</div>
      <div className="featured--seasons">{item.number_of_seasons} season{item.number_of_seasons != 1 ? 's' : '' }</div>
    </div>
    <div className="featured"></div>

  </div>
</section>
  );
}
