import React from "react";
import './FeaturedMovie.css';

export default ({item})=>{

let releaseDate= new Date(item.first_air_date);
let genres = [];
for (let i in item.genres){
  genres.push(item.genres[i].name)
}
let description = item.overview;
if(description.length > 200){
  description = description.subString(0,200)+'...';
}

  return (

<section className="featured" style={{
  backgroundSize:'cover',
  backgroundPosition:'center',
  backgroundImage:`url(https://image.tmdb.org/t/p/original${item.backdrop_path})`
}}>
  <div className="featured--vertical">
    <div className="featured--horizontal">
    <div className="featured--name">{item.name}</div>
    <div className="featured--info">
      <div className="featured--points">{item.vote_average} score</div>
      <div className="featured--year">{releaseDate.getFullYear()}</div>
      <div className="featured--seasons">{item.number_of_seasons} season{item.number_of_seasons != 1 ? 's' : '' }</div>
    </div>
    <div className="featured--description">{item.overview}</div>
    <div className="featured--buttons">
      <a className="featured--watchbutton" href=""> {'>'} Watch </a>
      <a className="featured--watchlistbutton" href=""> + Watch list </a>
    </div>
    <div className="featured--genres"><strong>Genres: </strong>{genres.join(', ')}</div>

    </div>
  </div>
</section>
  );
}
